--
-- Estrutura da tabela `skill_db`
--

CREATE TABLE IF NOT EXISTS `skill_db` (
  `skill_id` int(11) unsigned NOT NULL DEFAULT '0',
  `range` int(11) unsigned NOT NULL DEFAULT '0',
  `hit` smallint(11) NOT NULL DEFAULT '0',
  `inf` mediumint(8) unsigned NOT NULL DEFAULT '0',
  `element` smallint(4) unsigned NOT NULL DEFAULT '0',
  `nk` text NOT NULL,
  `splash` int(11) unsigned NOT NULL DEFAULT '0',
  `maxlvl` int(11) unsigned NOT NULL DEFAULT '0',
  `nhits` smallint(6) unsigned NOT NULL DEFAULT '0',
  `castinterrupt` smallint(6) unsigned NOT NULL DEFAULT '0',
  `defreduction` smallint(6) unsigned NOT NULL DEFAULT '0',
  `inf2` text NOT NULL,
  `maxcount` smallint(4) unsigned NOT NULL DEFAULT '0',
  `attacktype` text NOT NULL,
  `blowcount` smallint(4) unsigned NOT NULL DEFAULT '0',
  `name` text NOT NULL,
  `description` text NOT NULL,
  PRIMARY KEY (`skill_id`)
) ENGINE=MyISAM DEFAULT CHARSET=latin1;

